import { AiOutlineSearch } from "react-icons/ai";
import { useContext } from 'react';
import { globalContext } from './context/Context';
import { useState } from "react";

export default function Header() {

    let { getData, menu, base_url, url, setUrl, search, setSearch, api_key } = useContext(globalContext)


    return (
        <div className='header'>
            <div className="header-container">
                <nav>
                    <ul>
                        {
                            menu.map((value, index) => {
                                return (
                                    <li key={index}>
                                        <a href="#" className="nav-link" onClick={(e) => { getData(e.target.name) }}>
                                            {value}
                                        </a>
                                    </li>
                                )
                            })
                        }
                    </ul>

                    <div className="input">
                        <input
                            type="text" placeholder='Enter Movie Name'

                            onChange={(e) => { setSearch(e.target.value) }} />
                        <AiOutlineSearch className='search-i' />
                    </div>

                </nav>
            </div>
        </div >
    )
}
