import React from 'react'
import { AiOutlineSearch } from "react-icons/ai";

export default function Header() {

    const menu = ['Popular','Kids','Drama','Comedie']

    const getChange = (item) =>{
     if( item.currentTarget.classList === 'a'){
        item.currentTarget.classList.remove('a')

     }else{
        item.currentTarget.classList.add('a')
     }
    }

    return (
        <div className='header'>
            <div className="header-container">
                <nav>
                    <ul>
                        {
                            menu.map((item,index) => {
                                return(
                                    <li className='a' onClick={(getChange)} key={index}> {item} </li>
                                )
                            })
                        }
                    </ul>

                    <div className="input">
                        <input type="text" placeholder='Enter Movie Name' />
                        <AiOutlineSearch className='search-i
                     '/>
                    </div>

                </nav>
            </div>
        </div>
    )
}
