import { createContext, useState, useEffect } from "react";
export const globalContext = createContext();

const GlobalContextProvider = ({ children }) => {

    const menu = ['Popular', 'Kids', 'Drama', 'Comedie']
    let api_key = '&api_key=baf73bb11d6b81a6179e684c79e3c09a';
    let base_url = 'https://api.themoviedb.org/3';
    let url = `${base_url}/discover/movie?sort_by=popularity.desc${api_key}`;
    let [get_data, setData] = useState([]);
    let [get_url, setUrl] = useState(url);
    let img_path = 'https://image.tmdb.org/t/p/w500'

    const getData = (type) => {
        if (type === 'Popular') {
            url = `${base_url}/discover/movie?sort_by=popularity.desc${api_key}`;
        }
        else if (type === 'Kids') {
            url = `${base_url}/discover/movie?certification_country=US&certification.lte=G&sort_by=popularity.desc${api_key}`;
        }
        else if (type === 'Drama') {
            url = `${base_url}/discover/movie?with_genres=18&sort_by=vote_average.desc&vote_count.gte=10${api_key}`;
        } else {
            url = `${base_url}/discover/movie?with_genres=35&with_cast=23659&sort_by=revenue.desc${api_key}`;
        }
    }

    useEffect(() => {
        fetch(get_url).then(res => res.json()).then(data => {
            setData(data.results);
        })
    }, [get_url])

    let values = {
        menu,
        api_key,
        base_url,
        url,
        get_data,
        setData,
        get_url,
        setUrl,
        img_path,
        getData
    }

    return (
        <globalContext.Provider value={values} >
            {children}
        </globalContext.Provider>
    )
}

export default GlobalContextProvider
