import { AiOutlineSearch } from "react-icons/ai";
import { useContext } from 'react';
import { globalContext } from './context/Context';

export default function Header() {

    let { getData, menu, search, setSearch, base_url, url, setUrl } = useContext(globalContext)

    const searchMovie = (evt) => {
        url = (`${base_url}/search/movie?api_key=baf73bb11d6b81a6179e684c79e3c09a&query=${search}`)
        setUrl(url)
    }

    return (
        <div className='header'>
            <div className="header-container">
                <nav>
                    <ul>
                        {
                            menu.map((value, index) => {
                                return (
                                    <li key={index}>
                                        <a href="#" className="nav-link" onClick={(e) => { getData(e.target.name) }}>
                                            {value}
                                        </a>
                                    </li>
                                )
                            })
                        }
                    </ul>

                    <div className="input">
                        <input
                            type="text" placeholder='Enter Movie Name'
                            onKeyPress={searchMovie}
                            value={search}
                            onChange={(e) => { search(e.target.value) }} />
                        <AiOutlineSearch className='search-i' />
                    </div>

                </nav>
            </div>
        </div >
    )
}
