import { createContext, useState, useEffect } from "react";
export const globalContext = createContext();

const GlobalContextProvider = ({ children }) => {

    const menu = ['Popular', 'Kids', 'Drama', 'Comedie']
    const api_key = '&api_key=baf73bb11d6b81a6179e684c79e3c09a';
    const base_url = 'https://api.themoviedb.org/3';
    const url = `${base_url}/discover/movie?sort_by=popularity.desc${api_key}`;
    const [get_data, setData] = useState([]);
    const [get_url, setUrl] = useState(url);

    useEffect(() => {
        fetch(get_url).then(res => res.json()).then(data => {
            setData(data.results);
        })
    }, [get_url])


    return (
        <globalContext.Provider >
            {children}
        </globalContext.Provider>
    )
}

export default GlobalContextProvider
